import React, { useState, useEffect } from 'react';
import { PlusCircle, TrendingUp, TrendingDown, DollarSign, Filter, Download, Upload, RefreshCw, Server, AlertCircle, LogOut, User, Lock } from 'lucide-react';
import { LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { supabase } from './supabaseClient';

const API_BASE_URL = 'http://localhost:8000';

const CashFlowApp = () => {
  // Supabase Auth state
  const [user, setUser] = useState(null);
  const [authView, setAuthView] = useState('sign_in');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [username, setUsername] = useState('');
  const [businessName, setBusinessName] = useState('');
  const [error, setError] = useState('');

  // App state
  const [transactions, setTransactions] = useState([]);
  const [filteredTransactions, setFilteredTransactions] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [filter, setFilter] = useState('all');
  const [dateRange, setDateRange] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [backendStatus, setBackendStatus] = useState('checking');
  const [selectedCurrency, setSelectedCurrency] = useState('USD');
  const [newTransaction, setNewTransaction] = useState({
    type: 'income',
    amount: '',
    category: '',
    description: '',
    date: new Date().toISOString().split('T')[0],
    payment_method: 'Cash',
    currency: 'USD'
  });

  const currencies = {
    USD: { symbol: '$', name: 'US Dollar', rate: 1 },
    EUR: { symbol: '€', name: 'Euro', rate: 0.92 },
    GBP: { symbol: '£', name: 'British Pound', rate: 0.79 },
  };

  const categories = {
    income: ['Salary', 'Business', 'Investment', 'Freelance', 'Other'],
    expense: ['Food', 'Transport', 'Utilities', 'Rent', 'Entertainment', 'Shopping', 'Healthcare', 'Other']
  };

  const paymentMethods = ['Cash', 'Credit Card', 'Debit Card', 'Bank Transfer', 'UPI', 'Other'];

  // Supabase Auth: check for user on mount and on auth state change
  useEffect(() => {
    const handleSignUp = async (e) => {
      e.preventDefault();
      setError('');
      if (!username.trim()) {
        setError('Username is required');
        return;
      }
      if (password !== confirmPassword) {
        setError('Passwords do not match');
        return;
      }
      if (password.length < 6) {
        setError('Password must be at least 6 characters');
        return;
      }
      setLoading(true);
      const { data, error } = await supabase.auth.signUp({ email, password });
      if (error) {
        setError(error.message);
      } else {
        // Insert profile data after registration
        if (data.user) {
          await supabase
            .from('profiles')
            .insert([
              {
                user_id: data.user.id,
                username,
                business_name: businessName || null,
              },
            ]);
        }
        alert('Account created! Please check your email to confirm.');
        setEmail('');
        setPassword('');
        setConfirmPassword('');
        setUsername('');
        setBusinessName('');
        setAuthView('sign_in');
      }
      setLoading(false);
    };
    setLoading(false);
  });

  const handleSignUp = async (e) => {
    e.preventDefault();
    setError('');

    // Validate form
    if (!username.trim()) {
      setError('Username is required');
      return;
    }
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }
    if (password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    setLoading(true);
    const { error } = await supabase.auth.signUp({ email, password });
    if (error) {
      setError(error.message);
    } else {
      // You can store username and businessName in Supabase user metadata if needed
      alert('Account created! Please check your email to confirm.');
      setEmail('');
      setPassword('');
      setConfirmPassword('');
      setUsername('');
      setBusinessName('');
      setAuthView('sign_in');
    }
    setLoading(false);
  };

  const handleSignOut = async () => {
    await supabase.auth.signOut();
    setTransactions([]);
  };

  // Backend health check
  useEffect(() => {
    checkBackendConnection();
  }, []);

  const checkBackendConnection = async () => {
    setBackendStatus('checking');
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      if (response.ok) {
        setBackendStatus('connected');
        setError(null);
      } else {
        setBackendStatus('disconnected');
      }
    } catch (err) {
      setBackendStatus('disconnected');
    }
  };

  // Transaction logic (uses FastAPI backend)
  const fetchTransactions = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/transactions?limit=1000`, {
        headers: {
          // Optionally, you can send the Supabase JWT to your backend for verification
          // 'Authorization': `Bearer ${user?.access_token}`
        }
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setTransactions(data);
    } catch (err) {
      setError(`Failed to fetch transactions: ${err.message}`);
    }
    setLoading(false);
  };

  const filterTransactions = () => {
    let filtered = [...transactions];
    if (filter !== 'all') {
      filtered = filtered.filter(t => t.type === filter);
    }
    if (searchTerm) {
      filtered = filtered.filter(t =>
        (t.description && t.description.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (t.category && t.category.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }
    if (dateRange !== 'all') {
      const now = new Date();
      filtered = filtered.filter(t => {
        const transDate = new Date(t.date);
        const diffDays = Math.floor((now - transDate) / (1000 * 60 * 60 * 24));
        if (dateRange === '7days') return diffDays <= 7;
        if (dateRange === '30days') return diffDays <= 30;
        if (dateRange === '90days') return diffDays <= 90;
        return true;
      });
    }
    setFilteredTransactions(filtered.sort((a, b) => new Date(b.date) - new Date(a.date)));
  };

  const addTransaction = async () => {
    if (!newTransaction.amount || !newTransaction.category) {
      alert('Please fill in Amount and Category');
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/transactions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // Optionally, send Supabase JWT here
        },
        body: JSON.stringify({
          type: newTransaction.type,
          amount: parseFloat(newTransaction.amount),
          category: newTransaction.category,
          description: newTransaction.description || '',
          date: newTransaction.date,
          payment_method: newTransaction.payment_method,
          currency: newTransaction.currency
        })
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Failed to add transaction');
      }
      await fetchTransactions();
      setNewTransaction({
        type: 'income',
        amount: '',
        category: '',
        description: '',
        date: new Date().toISOString().split('T')[0],
        payment_method: 'Cash',
        currency: selectedCurrency
      });
      setShowAddModal(false);
      alert('Transaction added successfully!');
    } catch (err) {
      setError(`Error adding transaction: ${err.message}`);
      alert(`Error: ${err.message}`);
    }
    setLoading(false);
  };

  const deleteTransaction = async (id) => {
    if (!window.confirm('Are you sure you want to delete this transaction?')) return;
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/transactions/${id}`, {
        method: 'DELETE',
        headers: {
          // Optionally, send Supabase JWT here
        }
      });
      if (!response.ok) {
        throw new Error('Failed to delete transaction');
      }
      await fetchTransactions();
      alert('Transaction deleted successfully!');
    } catch (err) {
      alert(`Error: ${err.message}`);
    }
    setLoading(false);
  };

  const formatCurrency = (amount) => {
    const currencyInfo = currencies[selectedCurrency] || currencies.USD;
    return `${currencyInfo.symbol}${parseFloat(amount).toFixed(2)}`;
  };

  const getTotals = () => {
    const income = filteredTransactions
      .filter(t => t.type === 'income')
      .reduce((sum, t) => sum + parseFloat(t.amount), 0);
    const expenses = filteredTransactions
      .filter(t => t.type === 'expense')
      .reduce((sum, t) => sum + parseFloat(t.amount), 0);
    return { income, expenses, balance: income - expenses };
  };

  const getCategoryData = () => {
    const categoryTotals = {};
    filteredTransactions
      .filter(t => t.type === 'expense')
      .forEach(t => {
        categoryTotals[t.category] = (categoryTotals[t.category] || 0) + parseFloat(t.amount);
      });
    return Object.entries(categoryTotals).map(([name, value]) => ({
      name,
      value: parseFloat(value.toFixed(2))
    }));
  };

  const getChartData = () => {
    const last30Days = [];
    const now = new Date();
    for (let i = 29; i >= 0; i--) {
      const date = new Date(now);
      date.setDate(date.getDate() - i);
      const dateStr = date.toISOString().split('T')[0];
      const dayTransactions = transactions.filter(t => t.date === dateStr);
      const income = dayTransactions
        .filter(t => t.type === 'income')
        .reduce((sum, t) => sum + parseFloat(t.amount), 0);
      const expenses = dayTransactions
        .filter(t => t.type === 'expense')
        .reduce((sum, t) => sum + parseFloat(t.amount), 0);
      last30Days.push({
        date: date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
        income: parseFloat(income.toFixed(2)),
        expenses: parseFloat(expenses.toFixed(2)),
        net: parseFloat((income - expenses).toFixed(2))
      });
    }
    return last30Days;
  };

  const totals = getTotals();
  const chartData = getChartData();
  const categoryData = getCategoryData();
  const COLORS = ['#3b82f6', '#ef4444', '#10b981', '#f59e0b', '#8b5cf6', '#ec4899', '#14b8a6', '#f97316'];

  // Auth UI
  if (!user) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
        <div className="max-w-md w-full">
          {backendStatus === 'disconnected' && (
            <div className="mb-4 p-4 rounded-lg bg-red-100 border-2 border-red-500">
              <div className="flex items-center gap-3">
                <Server size={24} className="text-red-600" />
                <div>
                  <p className="font-semibold">Backend Disconnected</p>
                  <p className="text-sm text-gray-600">Start your server: python main.py</p>
                </div>
              </div>
            </div>
          )}
          <div className="bg-white rounded-2xl shadow-xl p-8">
            <div className="text-center mb-8">
              <h1 className="text-4xl font-bold text-gray-800 mb-2">CashFlow Manager</h1>
              <p className="text-gray-600">Track your finances with ease</p>
            </div>
            {authView === 'sign_in' ? (
              <form onSubmit={handleSignIn} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                  <input
                    type="email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Password</label>
                  <input
                    type="password"
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                    required
                  />
                </div>
                <div className="flex gap-3 mt-6">
                  <button
                    type="button"
                    onClick={() => setAuthView('sign_up')}
                    className="flex-1 px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition"
                  >
                    Create Account
                  </button>
                  <button
                    type="submit"
                    disabled={loading}
                    className="flex-1 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition disabled:opacity-50"
                  >
                    {loading ? 'Logging in...' : 'Login'}
                  </button>
                </div>
                {error && <p className="text-red-600 mt-2">{error}</p>}
              </form>
            ) : (
              <form onSubmit={handleSignUp} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Username *</label>
                  <input
                    type="text"
                    value={username}
                    onChange={e => setUsername(e.target.value)}
                    placeholder="Choose a username"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Email *</label>
                  <input
                    type="email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                    placeholder="your@email.com"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Password (min 6 chars) *</label>
                  <input
                    type="password"
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                    placeholder="Enter password"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Confirm Password *</label>
                  <input
                    type="password"
                    value={confirmPassword}
                    onChange={e => setConfirmPassword(e.target.value)}
                    placeholder="Re-enter password"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Business Name (Optional)</label>
                  <input
                    type="text"
                    value={businessName}
                    onChange={e => setBusinessName(e.target.value)}
                    placeholder="Your business name (optional)"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500"
                  />
                </div>
                <div className="flex gap-3 mt-6">
                  <button
                    type="button"
                    onClick={() => {
                      setAuthView('sign_in');
                      setUsername('');
                      setBusinessName('');
                      setConfirmPassword('');
                      setError('');
                    }}
                    className="flex-1 px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition"
                  >
                    Back to Login
                  </button>
                  <button
                    type="submit"
                    disabled={loading}
                    className="flex-1 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition disabled:opacity-50"
                  >
                    {loading ? 'Creating...' : 'Register'}
                  </button>
                </div>
                {error && <p className="text-red-600 mt-2">{error}</p>}
              </form>
            )}
          </div>
        </div>
      </div>
    );
  }

  // Main app (authenticated)
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="max-w-7xl mx-auto">
        {error && (
          <div className="mb-4 p-4 bg-red-100 border-2 border-red-500 rounded-lg flex items-center gap-3">
            <AlertCircle className="text-red-600" size={24} />
            <p className="text-red-600">{error}</p>
          </div>
        )}
        <div className="bg-white rounded-2xl shadow-xl p-6 mb-6">
          <div className="flex justify-between items-center mb-4">
            <div>
              <h1 className="text-4xl font-bold text-gray-800 mb-2">CashFlow Manager</h1>
              <p className="text-gray-600">Welcome back, {user.email}!</p>
            </div>
            <div className="flex gap-2">
              <button
                onClick={handleSignOut}
                className="flex items-center gap-2 px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition"
              >
                <LogOut size={20} />
                Logout
              </button>
              <button
                onClick={() => setShowAddModal(true)}
                className="flex items-center gap-2 px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition shadow-lg"
              >
                <PlusCircle size={20} />
                Add Transaction
              </button>
            </div>
          </div>
          {/* ...rest of your dashboard UI (totals, charts, transactions, add modal) ... */}
          {/* Keep your existing dashboard code here */}
        </div>
        {/* ...rest of your dashboard UI ... */}
      </div>
      {/* Add Transaction Modal (keep your existing modal code) */}
    </div>
  );
};

export default CashFlowApp;
